import React from "react";
import ReactDOM from "react-dom/client"; // ✅ Use 'client' for React 18
import { BrowserRouter } from "react-router-dom";
import { AuthProvider } from "./context/AuthContext";
import App from "./App";
import "./styles/global.css"; // Import Tailwind or global styles

const root = ReactDOM.createRoot(document.getElementById("root")); // ✅ Fix for React 18

root.render(
  <React.StrictMode>
    <AuthProvider>
      <BrowserRouter>
        <App />
      </BrowserRouter>
    </AuthProvider>
  </React.StrictMode>
);
