import React, { useState, useEffect } from "react";
import SearchBar from "../components/SearchBar";
import Map from "../components/map";
import { getNearestOffices } from "../services/getNearestOffices";

const Home = () => {
  const [userLocation, setUserLocation] = useState({ lat: 41.0082, lon: 28.9784 }); // Default: Istanbul
  const [offices, setOffices] = useState([]);

  // Get user's current location
  useEffect(() => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({ lat: position.coords.latitude, lon: position.coords.longitude });
        },
        (error) => console.error("Geolocation error:", error.message)
      );
    }
  }, []);

  // Fetch nearby offices when location updates
  useEffect(() => {
    const fetchOffices = async () => {
      try {
        const data = await getNearestOffices(userLocation.lat, userLocation.lon);
        setOffices(data);
      } catch (error) {
        console.error("Error fetching nearby offices:", error);
      }
    };

    fetchOffices();
  }, [userLocation]);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold">Welcome to Car Rental</h1>
      
      {/* Search Bar to find locations */}
      <SearchBar onSelect={(location) => setUserLocation(location)} />

      {/* Map with user location and rental offices */}
      <Map center={[userLocation.lat, userLocation.lon]} offices={offices} />

      {/* List of Nearby Offices */}
      <div className="mt-6">
        <h2 className="text-lg font-semibold">Nearby Rental Offices</h2>
        {offices.length === 0 ? (
          <p>No offices found nearby.</p>
        ) : (
          <ul>
            {offices.map((office) => (
              <li key={office.id}>
                {office.name} - {office.city} ({office.distance.toFixed(2)} km away)
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Home;
