import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import SearchBar from "../components/SearchBar";
import LanguageSwitcher from "../components/LanguageSwitcher";
import { getNearestOffices } from "../services/officeService";
import { useTranslation } from "react-i18next";

const Home = () => {
  const { user } = useContext(AuthContext);
  const { t } = useTranslation();
  const [offices, setOffices] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!user || !user.jwtToken) {
      console.error("User not authenticated");
      return;
    }
  
    navigator.geolocation.getCurrentPosition(
      async (position) => {
        try {
          const { latitude, longitude } = position.coords;
          console.log("Fetching offices for:", latitude, longitude);
          const data = await getNearestOffices(latitude, longitude, user.jwtToken);
          setOffices(data);
        } catch (error) {
          console.error("Error fetching offices:", error);
        }
      },
      (error) => console.error("Geolocation error:", error.message)
    );
  }, [user]);
  

  return (
    <div className="container mx-auto p-4">
      <header className="flex justify-between items-center">
        <h1 className="text-2xl font-bold">{t("Welcome")}</h1>
        <LanguageSwitcher />
      </header>

      {user && <p className="mt-2">{t("Hello")}, {user.name}!</p>}

      <SearchBar />

      <div className="mt-6">
        <h2 className="text-lg font-semibold">{t("Nearby Offices")}</h2>
        {offices.length === 0 ? (
          <p>{t("No offices found nearby.")}</p>
        ) : (
          <ul>
            {offices.map((office) => (
              <li key={office.id}>
                {office.name} - {office.city}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Home;
